'use strict';

const KB = 1024, MB = 1024 * 1024;

module.exports = {
  asKb(bytes) {
    if (!bytes || bytes < 0)
      return 0;
    return Number(bytes / KB).toFixed(1);
  },
  format(bytes) {
    if (!bytes || bytes < 0)
      return 'N/A';

    if (bytes < KB) {
      return Number(bytes) + ' B';
    } else if (bytes < MB) {
      return Number(bytes / KB).toFixed(1) + ' KB';
    } else {
      return Number(bytes / MB).toFixed(1) + ' MB';
    }
  }
};

'use strict';

module.exports = function ( word ) {
	return word.substr( 0, 1 ).toUpperCase() + word.substr( 1 );
};

'use strict';

const plural = require('./plural');

module.exports = {
  duration(seconds) {
    if (seconds === undefined) {
      return '';
    }

    var secondsPerYear = 365 * 24 * 60 * 60,
      secondsPerWeek = 60 * 60 * 24 * 7,
      secondsPerDay = 60 * 60 * 24,
      secondsPerHour = 60 * 60,
      secondsPerMinute = 60,
      sign = (seconds < 0) ? '-' : '';

    if (seconds < 0) {
      seconds = Math.abs(seconds);
    }

    if (seconds / secondsPerYear >= 1) {
      return sign + plural(Math.round(seconds / secondsPerYear), ' year');
    } else if (seconds / secondsPerWeek >= 1) {
      return sign + plural(Math.round(seconds / secondsPerWeek), ' week');
    } else if (seconds / secondsPerDay >= 1) {
      return sign + plural(Math.round(seconds / secondsPerDay), ' day');
    } else if (seconds / secondsPerHour >= 1) {
      return sign + plural(Math.round(seconds / secondsPerHour), ' hour');
    } else if (seconds / secondsPerMinute >= 1) {
      return sign + plural(Math.round(seconds / secondsPerMinute), ' minute');
    } else {
      return sign + plural(seconds, ' second');
    }
  },
  ms(ms) {
    return ms + ' ms'; 
  }
};

'use strict';

module.exports = function ( number, text ) {
	if ( number === 0 || number > 1 ) {
    text += 's';
	}
	return '' + number + ' ' + text;
};

'use strict';

module.exports = function(value) {
  value = value || 0;
  if (value > 90) {
    return 'ok';
  }
  else if (value > 80) {
    return 'warning';
  }
  return 'error';
};

