    })
    .option('gzipHAR', {
      describe: 'Compress the HAR files with GZIP.',
      default: false,
      type: 'boolean'
    })
    .option('outputFolder', {
      describe: 'The folder where the result will be stored.'
    })
    .option('firstParty', {
      describe: 'A regex running against each request and categorize it as first vs third party URL. (ex: ".*sitespeed.*")'
    })
    .option('utc', {
      describe: 'Use Coordinated Universal Time for timestamps',
      default: false,
      type: 'boolean'
    })
    .help('h')
    .alias('help', 'h')
    .config('config')
    .alias('version', 'V')
    .coerce('budget', function(arg) {
      if (typeof arg === 'object' && !Array.isArray(arg)) {
        if (arg.configPath) {
          arg.config = JSON.parse(fs.readFileSync(arg.configPath, 'utf8'));
        } else if (arg.config){
          arg.config = JSON.parse(arg.config);
        }
        return arg;
      } else {
        throw new Error('[ERROR] Something looks wrong with your budget configuration. Since sitespeed.io 4.4 you should pass the path to your budget file through the --budget.configPath flag instead of directly through the --budget flag.');
      }
    })
    .coerce('webpagetest', function(arg) {
      // for backwards compatible reasons we check if the passed parameters is a path to a script, if so just us it (PR #1445)
      if (arg.script && fs.existsSync(arg.script)) {
        arg.script = fs.readFileSync(path.resolve(arg.script), 'utf8');
        /* eslint no-console: off */
        console.log('[WARNING] Since sitespeed.io 4.4 you should pass the path to the script file through the --webpagetest.file flag (https://github.com/sitespeedio/sitespeed.io/pull/1445).');
        return arg;
      }

      if (arg.file) {
        arg.script = fs.readFileSync(path.resolve(arg.file), 'utf8');
      } else if (arg.script) {
        // because the escaped characters are passed re-escaped from the console
        arg.script = arg.script.split('\\t').join('\t');
        arg.script = arg.script.split('\\n').join('\n');
      }
      return arg;
    })
    //     .describe('browser', 'Specify browser')
    .wrap(yargs.terminalWidth())
    //  .check(validateInput)
    .epilog('Read the docs at https://www.sitespeed.io/documentation/sitespeed.io/');

  const aliases = parsed.getOptions().alias,
    argv = parsed.argv;

  // aliases are long options -> short option
  const aliasLookup = reduce(aliases, (lookup, value, key) => {
    lookup.set(value[0], key);
    return lookup;
  }, new Map());

  let explicitOptions = yargs.reset().argv;

  explicitOptions = reduce(explicitOptions, (result, value, key) => {
    if (aliasLookup.has(key)) {
      const fullKey = aliasLookup.get(key);
      result = set(result, fullKey, value);
    }
    result = set(result, key, value);
    return result;
  }, {});

  if (argv.config) {
    const config = require(path.resolve(process.cwd(), argv.config));
    explicitOptions = merge(explicitOptions, config);
  }

  if (argv.webpagetest.custom) {
    argv.webpagetest.custom = fs.readFileSync(path.resolve(argv.webpagetest.custom), {
      encoding: 'utf8'
    });
  }

  if (argv.summaryDetail) argv.summary = true;

  return {
    urls: cliUtil.getURLs(argv._),
    urlsMetaData: cliUtil.getAliases(argv._),
    options: argv,
    explicitOptions: explicitOptions
  };
};

'use strict';

const fs = require('fs');

module.exports = {
  getURLs(urls) {
    const allUrls = [];
    urls = urls.map((url) => url.trim());

    for (var url of urls) {
      if (url.startsWith('http')) {
        allUrls.push(url);
      } else {
        const filePath = url;
        var lines = fs.readFileSync(filePath).toString().split('\n');
        for (let line of lines) {
          if (line.trim().length > 0) {
            let lineArray = line.split(" ", 2);
            let url = lineArray[0].trim();
            if(url) {
              allUrls.push(url);
            }
          }
        }
      }
    }
    return allUrls;
  },
   getAliases(urls) {
    const urlMetaData = {};
    urls = urls.map((url) => url.trim());

    for (var url of urls) {
      if (url.startsWith('http')) {
        return {};
      } else {
        const filePath = url;
        var lines = fs.readFileSync(filePath).toString().split('\n');
        for (let line of lines) {
          if (line.trim().length > 0) {
            let url, alias = null;
            let lineArray = line.split(" ", 2);
            url = lineArray[0].trim();
            if(lineArray[1]) {
              alias = lineArray[1].trim();
            }
            if(url && alias) {
                urlMetaData[url] = {'alias' : alias};
            }
          }
        }
      }
    }
    return urlMetaData;
  }
};

'use strict';

const isEmpty = require('lodash.isempty');
const filterRegistry = require('../../support/filterRegistry');
const Sender = require('./sender');
const merge = require('lodash.merge');
const log = require('intel');
const sendAnnotations = require('./send-annotation');
const DataGenerator = require('./data-generator');

const defaultConfig = {
  port: 2003,
  namespace: 'sitespeed_io.default',
  includeQueryParams: false
};

module.exports = {
  open(context, options) {
    const opts = merge({}, defaultConfig, options.graphite);
    this.options = options;
    this.sender = new Sender(opts.host, opts.port);
    this.dataGenerator = new DataGenerator(opts.namespace, opts.includeQueryParams, options);
    log.debug('Setting up Graphite %s:%s for namespace %s', opts.host, opts.port, opts.namespace);
    this.timestamp = context.timestamp;
    this.storageManager = context.storageManager;
  },
  processMessage(message) {
    if (!(message.type.endsWith('.summary') || message.type.endsWith('.pageSummary')))
      return;

    // we only sends individual groups to Grahite, not the
    // total of all groups (you can calculate that yourself)
    if (message.group === 'total')  {
      return;
    }

    message = filterRegistry.filterMessage(message);
    if (isEmpty(message.data))
      return;

    // TODO Here we could add logic to either create a new timestamp or
    // use the one that we have for that run. Now just use the one for the
    // run
    let data = this.dataGenerator.dataFromMessage(message, this.timestamp).join('\n') + '\n';

    if (data.length > 0) {
      const storageManager = this.storageManager;
      const resultBaseURL = this.options.resultBaseURL;
      return this.sender.send(data).then(() => {
        // make sure we only send once per URL (and browsertime is the most important)
        // and you need to configure a base URL where you get the HTML result
        if (message.type === 'browsertime.pageSummary' && resultBaseURL) {
          return sendAnnotations.send(this.options, message.group, message.url, storageManager.getRelativeBaseDir(), storageManager.pathFromRootToPageDir(message.url), this.timestamp);
        } else return;
      });
    } else {
      return Promise.reject(new Error('No data to send to graphite for message:\n' +
        JSON.stringify(message, null, 2)));
    }
  },
  config: defaultConfig
};

'use strict';

const net = require('net'),
  log = require('intel'),
  Promise = require('bluebird');

class GraphiteSender {
  constructor(host, port) {
    this.host = host;
    this.port = port;
  }

  send(data) {
    log.info('Send data to Graphite %s:%s', this.host, this.port);
    log.trace('Sending ' + data);
    return new Promise((resolve, reject) => {
      let socket = net.connect(this.port, this.host, () => {
        socket.write(data);
        socket.end();
        resolve();
      });
      socket.on('error', reject);
    });
  }
}

module.exports = GraphiteSender;

'use strict';
const http = require('http');
const https = require('https');
const log = require('intel');
const Promise = require('bluebird');
const graphiteUtil = require('./util');

module.exports = {
  send(options, group, url, baseDir, pagePath, time) {

    // The tags make it possible for the dashboard to use the
    // templates to choose which annotations that will be showed.
    // That's why we need to send tags that matches the template
    // variables in Grafana.
    const connectivity = graphiteUtil.getConnectivity(options);
    const browser = options.browser;
    const namespace = options.graphite.namespace.split('.').join(',');
    const urlAndGroup = graphiteUtil.getURLAndGroup(options, group, url, options.graphite.includeQueryParams).split('.').join(',');
    const tags =  `${connectivity},${browser},${namespace},${urlAndGroup}`;
    const message = `<a href='${options.resultBaseURL}/${baseDir}/${pagePath}' target='_blank'>Result ${options.browsertime.iterations} run(s)</a>`;
    const timestamp = Math.round(time.valueOf() / 1000);
    const postData =
      `{"what": "Sitespeed.io", "tags": "${tags}", "data": "${message}", "when": ${timestamp}}`;
    const postOptions = {
      hostname: options.graphite.host,
      port: options.graphite.httpPort || 8080,
      path: '/events/',
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Content-Length': Buffer.byteLength(postData)
      }
    };

    // If Graphite is behind auth, use it!
    if (options.graphite.auth) {
      postOptions.auth = options.graphite.auth;
    }

    return new Promise((resolve, reject) => {
      log.trace('Send annotation to Graphite: %j', postData);
      // not perfect but maybe work for us
      const lib = options.graphite.httpPort === 443 ? https : http;
      const req = lib.request(postOptions, (res) => {
        if (res.statusCode !== 200) {
          log.error('Got %s from Graphite when sending annotation', res.statusCode);
          reject();
        } else {
        res.setEncoding('utf8');
        log.info('Sent annotation to Graphite');
        resolve();
      }
      });
      req.on('error', (err) => {
        log.error('Got error from Graphite when sending annotation', err);
        reject(err)
      });
      req.write(postData);
      req.end();
    })
  }
}

'use strict';

const get = require('lodash.get');
const flatten = require('../../support/flattenMessage');

module.exports = {
  toSafeKey(key) {
    return key.replace(/[.~ /+|,:?&%]|%7C/g, '_');
  },
  getConnectivity(options) {
    // if we have a friendly name for your conectivity, use that!
    let connectivity = get(options, 'browsertime.connectivity.alias');
    if (connectivity) {
      return this.toSafeKey(connectivity);
    } else {
      return options.connectivity;
    }
  },
  getURLAndGroup(options, group, url, includeQueryParams) {
    if(group && options.urlsMetaData && options.urlsMetaData[url] && options.urlsMetaData[url].alias) {
      let alias = options.urlsMetaData[url].alias;
       return this.toSafeKey(group) + "." + this.toSafeKey(alias);
    } else {
      return flatten.keypathFromUrl(url, includeQueryParams);
    }
  }
}

'use strict';

const urlParser = require('url');

function joinNonEmpty(strings, delimeter) {
  return strings.filter(Boolean).join(delimeter);
}

function toSafeKey(key) {
  return key.replace(/[.~ /+|,:?&%]|%7C/g, '_');
}

module.exports = {
  keypathFromUrl(url, includeQueryParams)
  {
    function flattenQueryParams(params) {
      return Object.keys(params).reduce((result, key) =>
          joinNonEmpty([result, key, params[key]], '_'),
        '');
    }

    url = urlParser.parse(url, !!includeQueryParams);

    let path = toSafeKey(url.pathname);

    if (includeQueryParams) {
      path = joinNonEmpty([path, toSafeKey(flattenQueryParams(url.query))], '_');
    }

    const keys = [
      toSafeKey(url.hostname),
      path
    ];

    return joinNonEmpty(keys, '.');
  },

  flattenMessageData(message) {
    function isNumeric(n) {
      return !isNaN(parseFloat(n)) && isFinite(n);
    }

    function recursiveFlatten(target, keyPrefix, value, type) {
      // super simple version to avoid flatten HAR and screenshot data
      if (keyPrefix.match(/(screenshots\.|har\.)/)) {
          return;
      }

      // Google is overloading User Timing marks
      // See https://github.com/sitespeedio/browsertime/issues/257
      if (keyPrefix.indexOf('userTimings.marks.goog_') > -1) {
        return;
      }

      // Google is overloading User Timing marks = the same using WebPageTest
      // See https://github.com/sitespeedio/browsertime/issues/257
      if (keyPrefix.indexOf('userTimes.goog_') > -1) {
         return;
      }

      const valueType = typeof value;

      switch (valueType) {
        case 'number':
        {
          target[keyPrefix] = value;
        }
          break;
        case 'object':
        {
          if (value === null) {
            break;
          }

          Object.keys(value).forEach((key) => {

            // Hey are you coming to the future from 1980s? Please don't
            // look at this code, it's a ugly hack to make sure we can send assets
            // to Graphite and don't send them with array position, instead
            // use the url to generate the key

            if (type === 'pagexray.pageSummary' && keyPrefix === 'assets') {
                recursiveFlatten(target, joinNonEmpty([keyPrefix, toSafeKey(value[key].url)], '.'), value[key], type);
            }
            else {
              recursiveFlatten(target, joinNonEmpty([keyPrefix, toSafeKey(key)], '.'), value[key], type);
            }
          });
        }
          break;
        case 'string':
        {
          if (isNumeric(value)) {
            target[keyPrefix] = parseFloat(value);
          }
        }
          break;
        case 'boolean':
        {
          target[keyPrefix] = value ? 1 : 0;
        }
          break;
        case 'undefined':
        {
          // take care of faulty values, add a log in the future
        }
          break;
        default:
          throw new Error('Unhandled value type ' + valueType + ' found when flattening data for prefix ' + keyPrefix);
      }
    }

    let retVal = {};
    recursiveFlatten(retVal, '', message.data, message.type);
    return retVal;
  }
};

'use strict';

const flatten = require('../../support/flattenMessage'),
  util = require('util'),
  graphiteUtil = require('./util'),
  reduce = require('lodash.reduce');

function keyPathFromMessage(message, options, includeQueryParams) {
  let typeParts = message.type.split('.');
  typeParts.push(typeParts.shift());

  // always have browser and connectivity in Browsertime and related tools
  if (message.type.match(/(^pagexray|^coach|^browsertime|^largestassets|^slowestassets|^aggregateassets|^domains)/)) {

    // if we have a friendly name for your conectivity, use that!
    let connectivity = graphiteUtil.getConnectivity(options);

    typeParts.splice(1, 0, connectivity);
    typeParts.splice(1, 0, options.browser);
  } else if (message.type.match(/(^webpagetest)/)) {
    if (message.connectivity) {
      typeParts.splice(2, 0, message.connectivity);
    }
    if (message.location) {
      typeParts.splice(2, 0, message.location);
    }
  }
  // if we get a URL type, add the URL
  if (message.url) {
    typeParts.splice(1, 0, graphiteUtil.getURLAndGroup(options, message.group, message.url, includeQueryParams));
  } else if (message.group) {
    // add the group of the summary message
    typeParts.splice(1, 0, graphiteUtil.toSafeKey(message.group));
  }

  return typeParts.join('.');
}

class GraphiteDataGenerator {
  constructor(namespace, includeQueryParams, options) {
    this.namespace = namespace;
    this.includeQueryParams = !!includeQueryParams;
    this.options = options;
  }

  dataFromMessage(message, time) {
    const timestamp = Math.round(time.valueOf() / 1000);

    const keypath = keyPathFromMessage(message, this.options, this.includeQueryParams);

    return reduce(flatten.flattenMessageData(message), (entries, value, key) => {
      const fullKey = util.format('%s.%s.%s', this.namespace, keypath, key);
      entries.push(util.format('%s %s %s', fullKey, value, timestamp));
      return entries;
    }, []);
  }
}

module.exports = GraphiteDataGenerator;

'use strict';

const flatten = require('../../support/flattenMessage');
const merge = require('lodash.merge');
const filterRegistry = require('../../support/filterRegistry');

const defaultConfig = {
  list: false,
  filterList: false
};

module.exports = {
  open(context, options) {
    this.options = merge({}, defaultConfig, options.metrics);
    this.metrics = {};
    this.storageManager = context.storageManager;
  },
  postOpen() {
    if (this.options.filter) {

      const filters = Array.isArray(this.options.filter) ? this.options.filter : [this.options.filter];

      for (let metric of filters) {
        // for all filters
        // cleaning all filters means (right now) that all
        // metrics are sent
        if (metric === '*+') {
          filterRegistry.clearAll();
        }
        // all registred types will be set as unmatching,
        // use it if you want to have a clean filter where
        // all types are removed and then you can add your own
        else if(metric === '*-') {
          let types = filterRegistry.getTypes();
          filterRegistry.clearAll();
          for (let type of types) {
            filterRegistry.registerFilterForType('-', type);
          }
        }
        else {
          let parts = metric.split('.');
          // the type is "always" the first two
          let type = parts.shift() + '.' + parts.shift();
          let filter = parts.join('.');
          let oldFilter = filterRegistry.getFilterForType(type);
          if (oldFilter && typeof oldFilter === 'object') {
            oldFilter.push(filter);
          } else {
            oldFilter = [filter];
          }
          filterRegistry.registerFilterForType(oldFilter, type);
        }
      }
    }

  },
  processMessage(message) {
    if (this.options.list) {
      if (!(message.type.endsWith('.summary') || message.type.endsWith('.pageSummary')))
        return;
      let flattenMess = flatten.flattenMessageData(message);
      for (let key of Object.keys(flattenMess)) {
        this.metrics[message.type + '.' + key] = 1;
      }
    } else {
      return
    }
  },
  close() {
    if (this.options.list) {
      this.storageManager.writeData('metrics.txt', Object.keys(this.metrics).join('\n'));
    }

    if (this.options.filterList) {
      let output = '';
      let filtersByType = filterRegistry.getFilters();
      for (let type of Object.keys(filtersByType)) {
        for (let filters of filtersByType[type]) {
            output+= type + '.' + filters + '\n';
        }
      }
      return this.storageManager.writeData('configuredMetrics.txt', output);
    }
  },
  config: defaultConfig
};

'use strict';

const throwIfMissing = require('../../support/util').throwIfMissing;
const Promise = require('bluebird');
const path = require('path');
const log = require('intel');
const Slack = require('node-slack');
const merge = require('lodash.merge');
const getAttachments = require('./attachements');
const getSummary = require('./summary');

Promise.promisifyAll(Slack.prototype);

const defaultConfig = {
  userName: 'Sitespeed.io',
  type: 'all',
  limitWarning: 90,
  limitError: 80,
  limitMetric: 'coachScore'
};

module.exports = {
  name() {
    return path.basename(__dirname);
  },
  open(context, options) {
    throwIfMissing(options.slack, ['hookUrl', 'userName'], 'slack');
    this.data = context.dataCollection;
  },
  close(options) {
    const slackOptions = merge({}, defaultConfig, options.slack);
    const slack = new Slack(slackOptions.hookUrl);
    const type = slackOptions.type;
    const pageErrors = [];
    let logo = "https://www.sitespeed.io/img/slack/sitespeed-logo-slack.png";

    let channel = slackOptions.channel;
    if (channel && !channel.startsWith('#')) {
      channel = `#${channel}`;
    }

    for (const url of Object.keys(this.data.urlPages)) {
      if (this.data.urlPages[url].errors) {
        pageErrors.push(this.data.urlPages[url].errors);
      }
    }

    let text = '';

    if (['summary', 'all', 'error'].includes(type)) {
      const sum = getSummary(this.data, pageErrors, options);
      text += sum.summaryText + '\n' + sum.errorText;
      logo = sum.logo;
    }

    let attachments = [];
    if (['url', 'all', 'error'].includes(type)) {
      attachments = getAttachments(this.data, slackOptions);
    }

    if (type === 'error' && pageErrors.length > 0 || type !== 'error') {
      log.debug('Sending message to Slack channel %s and username %s', slackOptions.channel, slackOptions.userName);
      return slack.sendAsync({
        text,
        icon_url: logo,
        channel,
        mrkdwn: true,
        username: slackOptions.userName,
        attachments
      })
    }
  },
  config: defaultConfig
};

'use strict';

const get = require('lodash.get');
const h = require('../../support/helpers');

function getMetric(metric) {
  if (metric.median) {
    return metric.median + ' ms' + ' (' + metric.max + ')'
  } else {
    return metric;
  }
}

module.exports = function(dataCollection, slackOptions) {
  const attachments = [];

  const resultsPerUrl = dataCollection.urlPages;

  for (let url of Object.keys(resultsPerUrl)) {
    const results = resultsPerUrl[url];
    const base = results.data;
    const metrics = {
      firstPaint: {
        name: 'First paint',
        metric: get(base.browsertime, 'pageSummary.statistics.timings.firstPaint')
      },
      speedIndex: {
        name: 'Speed Index',
        metric: get(base.browsertime, 'pageSummary.statistics.visualMetrics.SpeedIndex')
      },
      firstVisualChange: {
        name: 'First Visual Change',
        metric: get(base.browsertime, 'pageSummary.statistics.visualMetrics.FirstVisualChange')
      },
      visualComplete85: {
        name: 'Visual Complete 85%',
        metric: get(base.browsertime, 'pageSummary.statistics.visualMetrics.VisualComplete85')
      },
      lastVisualChange: {
        name: 'Last Visual Change',
        metric: get(base.browsertime, 'pageSummary.statistics.visualMetrics.LastVisualChange')
      },
      fullyLoaded: {
        name: 'Fully Loaded',
        metric: get(base.browsertime, 'pageSummary.statistics.timings.fullyLoaded')
      },
      domContentLoadedTime: {
        name: 'domContentLoadedTime',
        metric: get(base.browsertime, 'pageSummary.statistics.timings.pageTimings.domContentLoadedTime')
      },
      rumSpeedIndex: {
        name: 'RUM Speed Index',
        metric: get(base.browsertime, 'pageSummary.statistics.timings.rumSpeedIndex')
      },
      coachScore: {
        name: 'Coach score',
        metric: get(base.coach, 'pageSummary.advice.performance.score')
      },
      transferSize: {
        name: 'Page transfer size',
        metric: h.size.format(get(base.pagexray, 'pageSummary.transferSize'))
      },
      transferRequests: {
        name: 'Requests',
        metric: get(base.pagexray, 'pageSummary.requests')
      }
    };

    const fields = [];
    for (const key of Object.keys(metrics)) {
      const metric = metrics[key];
      if (metric.metric !== undefined) {
        fields.push({
          title: metric.name,
          value: getMetric(metric.metric),
          short: true
        })
      }
    }

    // add all the errors
    if (results.errors) {
      for (const key of Object.keys(results.errors)) {
        fields.push({
          title: key + ' error',
          value: results.errors[key],
          short: false
        })
      }
    }

    let color = 'good';

    const limitMetric = metrics[slackOptions.limitMetric];
    if (limitMetric && slackOptions.limitMetric === 'coachScore') {
      if (limitMetric.metric < slackOptions.limitError) {
        color = 'danger';
      } else if (limitMetric.metric < slackOptions.limitWarning) {
        color = 'warning';
      }
    }
    // SpeedIndex/firstVisualChange
    else if (limitMetric) {
      if (limitMetric.metric > slackOptions.limitError) {
        color = 'danger';
      } else if (limitMetric.metric > slackOptions.limitWarning) {
        color = 'warning';
      }
    }

    attachments.push({
      color,
      text: url,
      fields
    });
  }
  return attachments;
};

'use strict';
const util = require('util');
const get = require('lodash.get');
const h = require('../../support/helpers');

module.exports = function(dataCollection, errors, options) {
  const base = dataCollection.summaryPages.index || {};
  const metrics = {
    firstPaint: {
      name: 'First paint',
      metric: get(base.browsertime, 'summary.firstPaint.median')
    },
    domContentLoadedTime: {
      name: 'domContentLoadedTime',
      metric: get(base.browsertime, 'summary.pageTimings.domContentLoadedTime.median')
    },
    speedIndex: {
      name: 'Speed Index',
      metric: get(base.browsertime, 'summary.visualMetrics.SpeedIndex.median')
    },
    firstVisualChange: {
      name: 'First Visual Change',
      metric: get(base.browsertime, 'summary.visualMetrics.FirstVisualChange.median')
    },
    visualComplete85: {
      name: 'Visual Complete 85%',
      metric: get(base.browsertime, 'summary.visualMetrics.VisualComplete85.median')
    },
    lastVisualChange: {
      name: 'Last Visual Change',
      metric: get(base.browsertime, 'summary.visualMetrics.LastVisualChange.median')
    },
    fullyLoaded: {
      name: 'Fully Loaded',
      metric: get(base.browsertime, 'summary.fullyLoaded.median')
    },
    coachScore: {
      name: 'Coach score',
      metric: get(base.coach, 'summary.performance.score.median')
    },
    transferSize: {
      name: 'Page transfer weight',
      metric: h.size.format(get(base.pagexray, 'summary.transferSize.median'))
    }
  };

  let summaryText = `${h.plural(Object.keys(dataCollection.urlPages).length, 'page')} analyzed for ${h.short(options.urls[0], 30)} ` +
    `(${h.plural(options.browsertime.iterations, 'run')}, ` +
    `${h.cap(options.browsertime.browser)}/${options.mobile ? 'mobile' : 'desktop'}/${options.connectivity})\n`;

  summaryText += '*Site summary*\n';
  for (const key of Object.keys(metrics)) {
    if (metrics[key].metric !== undefined) {
      summaryText += metrics[key].name + ': ' + metrics[key].metric + '\n';
    }
  }

  let logo = 'https://www.sitespeed.io/img/slack/sitespeed-logo-slack.png';
  if (metrics.coachScore.median === 100) {
    logo = 'https://www.sitespeed.io/img/slack/sitespeed-logo-slack-100';
  }

  let errorText = '';
  if (errors.length > 0) {
    errorText += util.format('%d error(s):\n', errors.length);
    logo = 'https://www.sitespeed.io/img/slack/sitespeed-logo-slack-hm.png';
  }

  return {
    summaryText,
    errorText,
    logo
  };
};
